function ocutaElementosDoHeader(){document.querySelector("header").classList.add("header--is-hidden")}function exibeElementosDoHeader(){document.querySelector("header").classList.remove("header--is-hidden")}function removeBotaoAtivo(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__tabs__button--is-active")}document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?ocutaElementosDoHeader:exibeElementosDoHeader)()})});